const DashboardStyled = styled.div`

    .stats-con{
        display: grid;
        grid-template-columns: repeat(5, 1fr);
        gap: 2rem;
        .chart-con{
            grid-column: 1 / 4;
            height: 400px;
            .amount-con{
                display: grid;
                grid-template-columns: repeat(4, 1fr);
                gap: 2rem;
                margin-top: 2rem;
                .income, .expense{
                    grid-column: span 2;
                }
                .income, .expense, .balance{
                    background: #E0F7FA; /* Changed to a light cyan background */
                    border: 2px solid #B2EBF2; /* Border matching the background */
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1); /* Slightly darker shadow */
                    border-radius: 20px;
                    padding: 1rem;
                    p{
                        font-size: 3.5rem;
                        font-weight: 700;
                        color: #00796B; /* Changed text color to a teal shade */
                    }
                }

                .balance{
                    grid-column: 2 / 4;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    p{
                        color: #004D40; /* Darker teal for the balance */
                        opacity: 0.8;
                        font-size: 4.5rem;
                    }
                }
            }
        }

        .history-con{
            grid-column: 4 / -1;
            h2{
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
                color: #00796B; /* Teal for the history headings */
            }
            .salary-title{
                font-size: 1.2rem;
                color: #004D40; /* Dark teal for salary title */
                span{
                    font-size: 1.8rem;
                    color: #00796B; /* Matching the other teal colors */
                }
            }
            .salary-item{
                background: #E0F7FA; /* Light cyan background for salary items */
                border: 2px solid #B2EBF2; /* Matching border */
                box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1);
                padding: 1rem;
                border-radius: 20px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                p{
                    font-weight: 600;
                    font-size: 1.6rem;
                    color: #004D40; /* Dark teal for text */
                }
            }
        }
    }
`;

export default Dashboard;
