import React, { useState, useMemo } from 'react';
import styled from 'styled-components';
import bg from './img/bg.png';
import { MainLayout } from './styles/Layouts';
import Orb from './Components/Orb/Orb';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Expenses from './Components/Expenses/Expenses';
import Register from './Components/auth/register'; // Import the Register component
import { useGlobalContext } from './context/globalContext';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

function App() {
  const [active, setActive] = useState(1);

  const global = useGlobalContext();
  console.log(global);

  const displayData = () => {
    switch (active) {
      case 1:
        return <Dashboard />;
      case 2:
        return <Dashboard />;
      case 3:
        return <Income />;
      case 4:
        return <Expenses />;
      default:
        return <Dashboard />;
    }
  };

  const orbMemo = useMemo(() => {
    return <Orb />;
  }, []);

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <AppStyled bg={bg} className="App">
              {orbMemo}
              <MainLayout>
                <Navigation active={active} setActive={setActive} />
                <main>
                  {displayData()}
                </main>
              </MainLayout>
            </AppStyled>
          }
        />
        <Route path="/register" element={<StandalonePage component={Register} />} />
      </Routes>
    </Router>
  );
}

// Wrapper for standalone components like Register
function StandalonePage({ component: Component }) {
  return (
    <StandaloneStyled>
      <Component />
    </StandaloneStyled>
  );
}

// Styles for main application layout
const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  display: flex;
  flex-direction: column;

  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #ffffff;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    padding: 20px;
    &::-webkit-scrollbar {
      width: 8px;
    }
  }
`;

// Styles for standalone pages like Register
const StandaloneStyled = styled.div`
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
`;

export default App;
