// src/pages/login.jsx
import { useForm, FormProvider, Controller } from "react-hook-form";
import { Button } from './button';
import { Input } from './input';
import axios from 'axios';
import * as React from "react";
import './form.css';

import * as z from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { useNavigate } from "react-router-dom";
//import { auth, provider, signInWithPopup } from '../firebase';

// Schéma de validation du formulaire
const formSchema = z.object({
    email: z.string()
      .email('Invalid email address')
      .min(8, 'Email must be at least 8 characters')
      .max(30, 'Email must be at most 30 characters'),
    password: z.string()
      .min(5, 'Password must be at least 5 characters')
      .max(17, 'Password must be at most 17 characters'),
});

export function Login() {
  const methods = useForm({
    resolver: zodResolver(formSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });
  const navigate = useNavigate(); 
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState('');

  const onSubmit = async (values) => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://127.0.0.1:000/api/login', values, {
        withCredentials: true // Envoyer les cookies avec la requête
      });
      console.log(response.status);
      if (response.status === 204) {
        navigate('/');
      }
    } catch (error) {
      console.log(error);
      setError('Login failed. Please check your credentials and try again.');
    } finally {
      setLoading(false);
    }
  };

 /* const handleGoogleLogin = () => {
    signInWithPopup(auth, provider)
      .then((result) => {
        const user = result.user;
        user.getIdToken().then((token) => {
          axios.post('http://127.0.0.1:8000/api/google-login', {
            token,
          }).then((response) => {
            console.log('Google login success:', response.data);
            window.localStorage.setItem('ACCES_TOKEN', response.data.accessToken);
            navigate('/');
          }).catch((error) => {
            console.error('Google login error:', error);
            setError('Google login failed. Please try again.');
          });
        });
      })
      .catch((error) => {
        console.error('Google login error:', error);
        setError('Google login failed. Please try again.');
      });
  }; */

  React.useEffect(() => {
    console.log(methods.getValues()); // Log form values on render
  }, [methods]);

  return (
    <div className="form-container">
      <div className="form-header">
        <h1>Login</h1>
      </div>
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)} className="space-y-8">
          <div className="form-item">
            <label htmlFor="email">Email</label>
            <Controller
              name="email"
              control={methods.control}
              render={({ field }) => (
                <Input id="email" placeholder="Enter your email" {...field} />
              )}
            />
            {methods.formState.errors.email && (
              <p className="error-message">{methods.formState.errors.email.message}</p>
            )}
          </div>
          <div className="form-item">
            <label htmlFor="password">Password</label>
            <Controller
              name="password"
              control={methods.control}
              render={({ field }) => (
                <Input id="password" type="password" placeholder="Enter your password" {...field} />
              )}
            />
            {methods.formState.errors.password && (
              <p className="error-message">{methods.formState.errors.password.message}</p>
            )}
          </div>
          {error && <p className="error-message">{error}</p>}
          <div className="form-button">
            <Button type="submit" disabled={loading}>
              {loading ? <div className="loader"></div> : 'Login'}
            </Button>
          </div>
        </form>
        <div className="form-button">
        
        </div>
      </FormProvider>
    </div>
  );
}

export default Login;
