import { useForm, FormProvider, Controller } from "react-hook-form";
import axios from 'axios';
import * as React from "react";
import * as z from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { useNavigate } from "react-router-dom";

// Schéma de validation du formulaire
const formSchema = z.object({
    email: z.string()
      .email('Invalid email address')
      .min(8, 'Email must be at least 8 characters')
      .max(30, 'Email must be at most 30 characters'),
    password: z.string()
      .min(5, 'Password must be at least 5 characters')
      .max(17, 'Password must be at most 17 characters'),
});

export function Login() {
  const methods = useForm({
    resolver: zodResolver(formSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });
  const navigate = useNavigate(); 
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState('');

  const onSubmit = async (values) => {
    setLoading(true);
    setError('');
  
    console.log('Données envoyées:', values); // Log les données envoyées
  
    try {
      const response = await axios.post('http://localhost:5000/api/login', values, {
        withCredentials: true
      });
      console.log(response.status);
      if (response.status === 200) {
        navigate('/');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion:', error);
      setError('Login failed. Please check your credentials and try again.');
    } finally {
      setLoading(false);
    }
  };

  React.useEffect(() => {
    console.log(methods.getValues()); // Log form values on render
  }, [methods]);

  return (
    <div style={styles.formContainer}>
      <div style={styles.formHeader}>
        <h1>Login</h1>
      </div>
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)} style={styles.form}>
          <div style={styles.formItem}>
            <label htmlFor="email" style={styles.label}>Email</label>
            <Controller
              name="email"
              control={methods.control}
              render={({ field }) => (
                <input id="email" placeholder="Enter your email" {...field} style={styles.input} />
              )}
            />
            {methods.formState.errors.email && (
              <p style={styles.errorMessage}>{methods.formState.errors.email.message}</p>
            )}
          </div>
          <div style={styles.formItem}>
            <label htmlFor="password" style={styles.label}>Password</label>
            <Controller
              name="password"
              control={methods.control}
              render={({ field }) => (
                <input id="password" type="password" placeholder="Enter your password" {...field} style={styles.input} />
              )}
            />
            {methods.formState.errors.password && (
              <p style={styles.errorMessage}>{methods.formState.errors.password.message}</p>
            )}
          </div>
          {error && <p style={styles.errorMessage}>{error}</p>}
          <div style={styles.formButton}>
            <button type="submit" disabled={loading} style={styles.button}>
              {loading ? <div style={styles.loader}></div> : 'Login'}
            </button>
          </div>
        </form>
      </FormProvider>
    </div>
  );
}

// Styles for the Login component
const styles = {
  formContainer: {
    maxWidth: '400px',
    margin: '0 auto',
    padding: '20px',
    backgroundColor: '#fce4ec', // Light pink background
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  },
  formHeader: {
    textAlign: 'center',
    color: '#ad1457', // Dark pink for the header text
    marginBottom: '20px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  formItem: {
    marginBottom: '15px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
    color: '#880e4f', // Darker pink for labels
  },
  input: {
    width: '100%',
    padding: '10px',
    border: '1px solid #d81b60', // Pink border
    borderRadius: '5px',
    backgroundColor: '#f8bbd0', // Lighter pink for input background
    color: '#880e4f', // Darker pink for input text
  },
  errorMessage: {
    color: '#c2185b', // Reddish-pink for error messages
    fontSize: '0.875rem',
    marginTop: '5px',
  },
  formButton: {
    marginTop: '20px',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#ec407a', // Bright pink for the button
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  buttonHover: {
    backgroundColor: '#e91e63', // Darker pink on hover
  },
  buttonDisabled: {
    backgroundColor: '#f48fb1', // Lighter pink when disabled
    cursor: 'not-allowed',
  },
  loader: {
    border: '3px solid #f8bbd0', // Light pink
    borderTop: '3px solid #ad1457', // Dark pink
    borderRadius: '50%',
    width: '15px',
    height: '15px',
    animation: 'spin 1s linear infinite',
    margin: '0 auto',
  },
  '@keyframes spin': {
    '0%': { transform: 'rotate(0deg)' },
    '100%': { transform: 'rotate(360deg)' },
  },
};

export default Login;
