import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import avatar from '../../img/avatar.png';
import { signout } from '../../utils/Icons';
import { menuItems } from '../../utils/menuItems';

function Navigation({ active, setActive }) {
    // État pour le nom et la photo de l'utilisateur
    const [user, setUser] = useState({
        name: 'Mike', // Valeur par défaut avant récupération
        photo: avatar // Image par défaut
    });

    // Utilisation de useEffect pour récupérer les informations utilisateur
    useEffect(() => {
        // Remplacez cette fonction par la vôtre pour récupérer les données utilisateur
        const fetchUserData = async () => {
            try {
                // Exemple de récupération des données utilisateur (Remplacez par votre API)
                const response = await fetch('/api//user'); 
                const data = await response.json();
                
                // Mettre à jour l'état avec les données récupérées
                setUser({
                    name: data.name || 'Mike',  // Nom récupéré, ou 'Mike' par défaut
                    photo: data.photo || avatar  // Photo récupérée, ou avatar par défaut
                });
            } catch (error) {
                console.error('Erreur lors de la récupération des données utilisateur:', error);
            }
        };

        fetchUserData();
    }, []);

    return (
        <NavStyled>
            <div className="user-con">
                <img src={user.photo} alt="" />
                <div className="text">
                    <h2>{user.name}</h2>
                    <p>Your Money</p>
                </div>
            </div>
            <ul className="menu-items">
                {menuItems.map((item) => {
                    return (
                        <li
                            key={item.id}
                            onClick={() => setActive(item.id)}
                            className={active === item.id ? 'active' : ''}
                        >
                            {item.icon}
                            <span>{item.title}</span>
                        </li>
                    );
                })}
            </ul>
            <div className="bottom-nav">
                <li>
                    {signout} Sign Out
                </li>
            </div>
        </NavStyled>
    );
}

const NavStyled = styled.nav`
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100%;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;

    .user-con{
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;
        img{
            width: 80px;
            height: 80px;
            border-radius: 50%;
            object-fit: cover;
            background: #fcf6f9;
            border: 2px solid #FFFFFF;
            padding: .2rem;
            box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
        }
        h2{
            color: rgba(34, 34, 96, 1);
        }
        p{
            color: rgba(34, 34, 96, .6);
        }
    }

    .menu-items{
        flex: 1;
        display: flex;
        flex-direction: column;
        li{
            display: grid;
            grid-template-columns: 40px auto;
            align-items: center;
            margin: .6rem 0;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: rgba(34, 34, 96, .6);
            padding-left: 1rem;
            position: relative;
            {
                color: rgba(34, 34, 96, 0.6);
                font-size: 1.4rem;
                transition: all .4s ease-in-out;
            }
        }
    }

    .active{
        color: rgba(34, 34, 96, 1) !important;
        i{
            color: rgba(34, 34, 96, 1) !important;
        }
        &::before{
            content: "";
            position: absolute;
            left: 0;
            top: 0;
            width: 4px;
            height: 100%;
            background: #222260;
            border-radius: 0 10px 10px 0;
        }
    }

    .bottom-nav {
        li {
            display: flex;
            align-items: center;
            cursor: pointer;
            color: rgba(34, 34, 96, .6);
            font-weight: 500;
            transition: all .4s ease-in-out;
            i{
                margin-right: 1rem;
            }
        }
    }
`;

export default Navigation;
